import React, { useEffect, useState } from "react";
import {
  Table,
  TableHead,
  TableBody,
  TableRow,
  TableCell,
  Paper,
  Button,
  styled,
} from "@mui/material";

import axios from "axios";
import { Link } from "react-router-dom";

const StyledPaper = styled(Paper)({
  width: "90%",
  margin: "50px auto",
  overflow: "auto",
});

const StyledTable = styled(Table)({
  minWidth: 650,
});

const THead = styled(TableRow)({
  "& > th": {
    fontSize: 16,
    fontWeight: "bold",
    backgroundColor: "#f0f0f0",
  },
});

const TableCellOverflow = styled(TableCell)({
  overflow: "hidden",
  textOverflow: "ellipsis",
  whiteSpace: "nowrap",
});

const AllData = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    getAllDatas();
  }, []);

  const getAllDatas = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/items`);
      setUsers(response.data); // Assuming the response is an array of user data
    } catch (error) {
      console.error("Error fetching user data:", error);
    }
  };

  const deleteUserDetails = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/items/${id}`);
      getAllDatas();
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  return (
    <StyledPaper elevation={3}>
      <StyledTable>
        <TableHead>
          <THead>
            <TableCell>Id</TableCell>
            <TableCell>Title</TableCell>
            <TableCell>Description</TableCell>
            <TableCell>Actions</TableCell>
          </THead>
        </TableHead>
        <TableBody>
          {users.map((user) => (
            <TableRow key={user.id}>
              <TableCellOverflow>{user.id}</TableCellOverflow>
              <TableCellOverflow>{user.title}</TableCellOverflow>
              <TableCellOverflow>{user.description}</TableCellOverflow>
              <TableCell>
                <Button
                  variant="contained"
                  style={{ marginRight: 10 }}
                  component={Link}
                  to={`/edit/${user.id}`}
                >
                  Edit
                </Button>
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={() => deleteUserDetails(user.id)}
                >
                  Delete
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </StyledTable>
    </StyledPaper>
  );
};

export default AllData;
